# Process the data
data = []
for key, (melo_0, melo_100) in payoff_vectors_21.items():
    # Extract the count of MELO_0_100 from the key
    if "MELO_0_100:10" in key:
        count_0 = 10
        count_100 = 0
    elif "MELO_100_0:10" in key:
        count_0 = 0
        count_100 = 10
    else:
        parts = key.split(",")
        count_0 = int(parts[0].split(":")[1].strip("]"))
        count_100 = int(parts[1].split(":")[1].strip("]"))

    # Calculate means and standard errors
    mean_0 = np.mean(melo_0) if melo_0 else np.nan
    std_err_0 = np.std(melo_0, ddof=1)/np.sqrt(len(melo_0)) if melo_0 else 0

    mean_100 = np.mean(melo_100) if melo_100 else np.nan
    std_err_100 = np.std(melo_100, ddof=1) / \
        np.sqrt(len(melo_100)) if melo_100 else 0

    data.append({
        'count_0': count_0,
        'count_100': count_100,
        'mean_0': mean_0,
        'std_err_0': std_err_0,
        'mean_100': mean_100,
        'std_err_100': std_err_100,
        'label': f"M-ELO:{count_0}, CDA:{count_100}"
    })

# Sort by count_0 ascending
data.sort(key=lambda x: x['count_0'])

# Prepare data for plotting
labels = [d['label'] for d in data]
means_0 = [d['mean_0'] if not np.isnan(d['mean_0']) else 0 for d in data]
